import React, { useState } from 'react';
import axios from 'axios';

interface RegisterFormData {
  userName: string;
  userEmail: string;
  password: string;
  userMobileNum: string;
}

const Register: React.FC = () => {
  const [formData, setFormData] = useState<RegisterFormData>({
    userName: '', userEmail: '', password: '', userMobileNum: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/users/register', formData);
      alert(response.data.message);
    } catch (error) {
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="userName" value={formData.userName} onChange={handleChange} placeholder="Name" required />
      <input name="userEmail" value={formData.userEmail} onChange={handleChange} placeholder="Email" required />
      <input name="password" type="password" value={formData.password} onChange={handleChange} placeholder="Password" required />
      <input name="userMobileNum" value={formData.userMobileNum} onChange={handleChange} placeholder="Mobile Number" required />
      <button type="submit">Register</button>
    </form>
  );
};

export default Register;
